sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
var imp = g_form.getValue('u_impact'); // Field for Impact
var urg = g_form.getValue('u_urgency'); // Field for Urgency

    // Determine Priority based on Impact and Urgency
    if (imp == '1' && urg == '1') {
        g_form.setValue('u_priority', '1'); // Critical
    } else if (imp == '1' && urg == '2') {
        g_form.setValue('u_priority', '2'); // High
    } else if (imp == '1' && urg == '3') {
        g_form.setValue('u_priority', '3'); // Moderate
    } else if (imp == '2' && urg == '1') {
        g_form.setValue('u_priority', '2'); // High
    } else if (imp == '2' && urg == '2') {
        g_form.setValue('u_priority', '3'); // Moderate
    } else if (imp == '2' && urg == '3') {
        g_form.setValue('u_priority', '4'); // Low
    } else if (imp == '3' && urg == '1') {
        g_form.setValue('u_priority', '3'); // Moderate
    } else if (imp == '3' && urg == '2') {
        g_form.setValue('u_priority', '4'); // Low
    } else if (imp == '3' && urg == '3') {
        g_form.setValue('u_priority', '5'); // Planning
    }   

}
