sys_script_client.script:
function onSubmit() {
    // Retrieve values of the fields
    var dateOccurred = g_form.getValue('u_date_occcured'); // Field for date the incident occurred
    var incidentReportedDate = g_form.getValue('u_incident_reported_date'); // Field for incident reported date

    // Check if both fields have values
    if (dateOccurred && incidentReportedDate) {
        var occurredDate = new Date(dateOccurred);
        var reportedDate = new Date(incidentReportedDate);

        // Validate the dates
        if (reportedDate < occurredDate) {
            g_form.addErrorMessage('The Incident Reported Date cannot be earlier than the Date Occurred.');
            return false; // Prevent form submission
        }
    }

    return true; // Allow form submission if validation passes
}
