sys_script_client.script:
function onLoad() {
    const reasonFields = {
        "1": "u_reason_for_new",
        "2": "u_reason_for_in_progress",
        "3": "u_reason_on_hold",
        "6": "u_reason_for_resolved",
        "7": "u_reason_for_closed",
        "8": "u_reason_for_cancelled"
    };

    // Reset all fields
    Object.values(reasonFields).forEach(field => {
        g_form.setMandatory(field, false);
        g_form.setDisplay(field, false);
    });

    const selectedField = reasonFields[g_form.getValue("u_status")];

    if (selectedField) {
        g_form.setMandatory(selectedField, true);
        g_form.setDisplay(selectedField, true);
    }
}




