sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

// Get the values of the fields
    var victim = g_form.getValue('u_victim'); // 'u_victim' refers to the Caller/Employee Name field
    var assignedTo = g_form.getValue('u_assignment_to'); // 'u_assignment_to' refers to the Assigned To field

    // Check if both fields have the same value
    if (victim === assignedTo) {
        g_form.addInfoMessage('Caller and Assigned To are the same. Please verify if this is correct.');
    } else {
        // Clear the message if the values are no longer the same
        g_form.clearMessages();
    }
}
