sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Day 1 on scripting
    //if (newValue == 'true') {
    // alert("The checkbox is checked.");
    //} else {
    //alert("The checkbox is unchecked.");
    //}

    //Day 2 on scripting
    var act = g_form.getValue('u_active');
    if (act === 'true') {
        g_form.addDecoration('u_type_of_harassment', 'icon-star', 'Active is checked', 'color-green');
        g_form.removeDecoration('u_type_of_harassment', 'icon-script', 'Active is Unchecked', 'color-red');
        g_form.clearMessages();
        g_form.addErrorMessage('Active is checked');
        g_form.setSectionDisplay('related_records', true);

    } else if (act === 'false') {
        g_form.removeDecoration('u_type_of_harassment', 'icon-star', 'Active is checked', 'color-green');
        g_form.addDecoration('u_type_of_harassment', 'icon-script', 'Active is Unchecked', 'color-red');
        g_form.clearMessages();
        g_form.addInfoMessage('Active is unchecked');
        g_form.setSectionDisplay('related_records', false);
    }
}
