sys_script_client.script:
// Function to add or remove a choice based on a condition
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Exit the function if the form is still loading or the new value is empty
    if (isLoading || newValue === '') {
        return;
    }

    // Determine the logic based on the newValue
	var latestValue = g_form.getValue('u_active');

    if (latestValue === 'true') {
        // Add "Active Choice" and remove "IN Active Choice"
        g_form.addOption('u_type_of_harassment', 'act', 'Active Choice', 3);
        g_form.removeOption('u_type_of_harassment', 'inact');
    } else {
        // Add "IN Active Choice" and remove "Active Choice"
        g_form.addOption('u_type_of_harassment', 'inact', 'IN Active Choice', 5);
        g_form.removeOption('u_type_of_harassment', 'act');
    }
}
