sys_script_client.script:
function onSubmit() {
    // Clear any existing field messages for the 'Description' field
    g_form.clearMessages();
    g_form.hideFieldMsg('u_detailed_description');

    // Get the value of the 'Description' field
    var description = g_form.getValue('u_detailed_description');

    // Check if the 'Description' field is empty
    if (description.trim() == '') {
        // Display an error message
        g_form.showFieldMsg('u_detailed_description', 'Please fill in the Description field.', 'error');

        // Prevent form submission
        return false;
    }

    // Allow form submission if the Description field is populated
    return true;
}
