sys_script_client.script:
// Function to conditionally show or hide the attachment section
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Exit the function if the form is still loading or if the new value is empty
    if (isLoading || newValue === '') {
        return;
    }

    // Retrieve the value of the 'u_active' field
    var latestValue = g_form.getValue('u_active');

    // Check if the 'u_active' field is true or false and enable/disable attachments accordingly
    if (latestValue === 'true') {
        // Enable the attachment section if 'u_active' is true
        g_form.enableAttachments();
    } else {
        // Disable the attachment section if 'u_active' is false
        g_form.disableAttachments();
    }
}
