sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    // Get the value of the checkbox
    var isVerified = g_form.getValue('u_incident_date_verified_by_witness') == 'true';

    // If the checkbox is checked
    if (isVerified) {
        // Make 'Date occurred' read-only
        g_form.setReadOnly('u_date_occcured', true);
        // Clear any existing info messages
        g_form.clearMessages();
        g_form.addInfoMessage('Verification has been completed and Date Occured field is now read-only');

    } else {
        // If unchecked, make 'Date occurred' editable
        g_form.setReadOnly('u_date_occcured', false);
        // Show an info message
        g_form.clearMessages();
        g_form.addErrorMessage('Verification is pending, Hence Date field cannot be made read-only.');

    }
}
