sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
  // Run on load and on user change
  var state = g_form.getValue('u_status');
  var lock = (state === '8'); // Cancelled

  var fields = g_form.getEditableFields();
  for (var i = 0; i < fields.length; i++) {
    if (fields[i] !== 'u_status') g_form.setReadOnly(fields[i], lock);
  }
}
