sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var em = g_form.getValue('u_victim_email');

    var ga = new GlideAjax('global.UserAttributes'); //script include call
    ga.addParam('sysparm_name', 'uservalues'); //function call		
    ga.addParam('carrier', em); //equalising the var	// email value pawankalyan = abc(email);
    ga.getXML(pop);


    function pop(response) {

        var serverresults = (response.responseXML.documentElement.getAttribute("answer"));
        alert(serverresults);
		var result = serverresults.split("&");

        alert("array value is" +result);

        alert(result[0]);
        alert(result[1]);
        alert(result[2]);
        alert(result[3]);


        g_form.setValue('u_victim_first_name', result[0]);
        g_form.setValue('u_victim_last_name', result[1]);
        g_form.setValue('u_manager', result[2]);
        g_form.setValue('u_victim_phone_number', result[3]);

    }
}
