sys_script_client.script:
function onLoad() {
    var state = g_form.getValue('u_status'); // Get the current value of the 'state' field

    // Check if the incident state is 'Cancelled'
    if (state == '8') {
        // Set all fields as read-only
        var allFields = g_form.getEditableFields(); // Get all editable fields on the form
        for (var i = 0; i < allFields.length; i++) {
            g_form.setReadonly(allFields[i], true); // Set each field as read-only
        }
    }
}


