sys_script_client.script:
function onSubmit() {
  // Keywords to check in the Short Description (adjust as needed)
  var keywords = ['software', 'application'];

  // Use your actual field; OOTB is 'short_description'
  var text = (g_form.getValue('u_short_notes') || '').toLowerCase();

  var hasMatch = keywords.some(function (k) {
    return text.includes(k);
  });

  if (hasMatch) {
    // Use the actual sys_id of your Software group
    var softwareGroupSysId = 'db3ca84083c61210e456bed6feaad305';

    // Use 'assignment_group' if it's a normal Task table
    g_form.setValue('u_assignment_group', softwareGroupSysId);

    g_form.addInfoMessage('Assigned to Software group based on keywords.');
  }

  return true; // allow the submit to proceed
}
