sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   {

    // Keywords to check in the Short Description
    var keywords = ['software', 'application'];
    var shortDescription = g_form.getValue('u_short_notes'); // Get the Short Description field value

    // Check if the Short Description contains any of the keywords
    var shouldAssignToSoftwareGroup = keywords.some(function(keyword) {
        return shortDescription.toLowerCase().includes(keyword);
    });

    // If a keyword is found, set the Assignment Group to 'Software'
    if (shouldAssignToSoftwareGroup) {
        var softwareGroupSysId = 'db3ca84083c61210e456bed6feaad305'; // Replace with the sys_id of the 'Software' group
        g_form.setValue('u_assignment_group', db3ca84083c61210e456bed6feaad305);
        g_form.addInfoMessage('Ticket has been assigned to the Software group based on the keywords.');
    }

}

}
