sys_script_client.script:
// Client Script Type: onLoad
// Impersonate to the Assigned User to test this script //
function onLoad() {
    // Get the current logged-in user's sys_id
    var loggedInUser = g_user.userID;
    // Get the 'Assigned To' field's sys_id
    var assignedTo = g_form.getValue('u_assignment_to');
    // Check if the 'Assigned To' is the same as the logged-in user
    if (assignedTo == loggedInUser) {
        // Make the Description field read-only
        g_form.setReadOnly('u_detailed_description', true);
    }
}
