sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Allow execution on load so decorations persist after Save/Submit.
    if (isTemplate || (newValue === '' && !isLoading)) return;

    // Day 2 on scripting
    var act = g_form.getValue('u_active');
    var isLoad = (isLoading === true); // suppress noisy messages during load

    if (act === 'true') {
        // Decorations + attachments always applied (load or change)
        g_form.addDecoration('u_type_of_harassment', 'icon-star', 'Active is checked', 'color-green');
        g_form.removeDecoration('u_type_of_harassment', 'icon-script', 'Active is Unchecked', 'color-red');
        g_form.enableAttachments();

        // Keep your option logic exactly as-is
        g_form.addOption('u_status', 'act', 'Posh Active', 4);
        g_form.removeOption('u_status', 'inact', 'Posh Inactive');

        // Messages only on user-triggered change (not on initial load)
        if (!isLoad) {
            g_form.clearMessages();
            g_form.addErrorMessage('Active is checked');
        }
    } else if (act === 'false') {
        // Keep your option logic exactly as-is
        g_form.removeOption('u_status', 'act', 'Posh Active');
        g_form.addOption('u_status', 'inact', 'Posh Inactive', 2);

        // Decorations + attachments always applied (load or change)
        g_form.disableAttachments();
        g_form.removeDecoration('u_type_of_harassment', 'icon-star', 'Active is checked', 'color-green');
        g_form.addDecoration('u_type_of_harassment', 'icon-script', 'Active is Unchecked', 'color-red');

        // Messages only on user-triggered change
        if (!isLoad) {
            g_form.clearMessages();
            g_form.addInfoMessage('Active is unchecked');
        }
    }
}

