sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var victimEmail = g_form.getValue('u_victim_email');

    // Call the script include
    var glideAjax = new GlideAjax('VictimDetails');
    glideAjax.addParam('sysparm_name', 'getVictimDetails');
    glideAjax.addParam('email', victimEmail.trim()); // Trim removes spaces
    glideAjax.getXML(pop);

    function pop(response) {
        var serverResponse = response.responseXML.documentElement.getAttribute("answer");

        if (serverResponse) {
            var details = serverResponse.split("&");
            if (details.length === 4) {
                g_form.setValue('u_victim_first_name', details[0]);
                g_form.setValue('u_victim_last_name', details[1]);
                g_form.setValue('u_victim_phone_number', details[2]);
                g_form.setValue('u_manager', details[3]);
            } else {
                alert("Invalid response format. Please contact your administrator.");
            }
        } else {
            alert("No matching user found for the provided email.");
        }
    }
}
